import React, {Fragment} from 'react';
import styled from 'styled-components';

const Button = styled.button.attrs(props => ({
	left: props.left || "0",
  }))`
	::before {
		content: '';
		border-radius: 40px;
		background-image: linear-gradient(135deg, #00aaf4 , #00d258);
		padding: 32.5px 118px;
		top: -3px;
		right: -3px;
		position: absolute;
		z-index: -1;
	  }
	  :hover, :focus {
		color: #ffffff;
		background-image: linear-gradient(135deg, #00aaf4 , #00d258);
		outline:0;
	  }
	color: #00aaf4;
	font-size: 23px;
	font-family:  cera_probold, Arial, Helvetica, sans-serif;
	width: 230px;
	padding: 15px 0px;
	border-radius: 35px;
	border: none;
	cursor: pointer;
	margin-bottom: 20px;
	position: relative;
	margin-left: ${props => props.left};
`;

const button = (props) => (
	<Fragment>
		<Button onClick={props.click}>{props.text}</Button>
	</Fragment>
)

export default button;
