import React, { Component } from 'react';
import styled from 'styled-components';

const Container = styled.div`
	background: #000;
	padding-top: 21px;
`;

const Video = styled.video`
	height: 720px;
	width: 1280px;
	background: #0b0102;
	position: absolute;
	bottom: 56px;
	left: 0px;
	z-index: 1;
`;

const Placeholder = styled.div`
	height: 720px;
	width: 1280px;
	position: absolute;
	bottom: 56px;
	left: 0px;
	z-index: 2;
	background: #0b0102;
`;

class Watch extends Component {
	constructor(props) {
		super(props);
		this.state = { play: false };
	}

	changeVolume = () => {
		this.video.volume = .5;
		this.placeholder.style.display = 'none';
	}

	componentDidMount() {
		// this.play();
	}

	render() {
		return (
			<Container>
				<Placeholder ref={(placeholder) => { this.placeholder = placeholder }} />
				<Video src="assets/video/watch.mp4" ref={(video) => { this.video = video }} onPlay={this.changeVolume} autoPlay />
			</Container>
		)
	};
}

export default Watch;
