import React, { Component} from 'react';
import styled from 'styled-components';


const SlideContainer = styled.div`
	width: 500px;
	margin: auto;
`;

const VolumeSlider = styled.input`
	:hover {
	opacity: 1; /* Fully shown on mouse-over */
	}	

	::-webkit-slider-thumb {
		-webkit-appearance: none;
		appearance: none;
		width: 25px;
		height: 25px;
		border-radius: 50%; 
		background: #4CAF50;
		cursor: pointer;
	}
	::-moz-range-thumb {
		width: 25px;
		height: 25px;
		border-radius: 50%;
		background: #4CAF50;
		cursor: pointer;
	}

	-webkit-appearance: none;
	width: 100%;
	height: 15px;
	border-radius: 5px;   
	background: #d3d3d3;
	outline: none;
	opacity: 0.7;
	-webkit-transition: .2s;
	transition: opacity .2s;	
`;


class Volume extends Component {
render () {
		return (
			<SlideContainer>
				<VolumeSlider type="range" min="1" max="100" value="50" onChange={this.changed}/>
			</SlideContainer>
		)
	}
}

export default Volume;
