import React, { Component } from 'react';
import styled from 'styled-components';

const Container = styled.div`
	background: #000;
	padding-top: 21px;
`;

const Placeholder = styled.div`
	height: 720px;
	width: 1280px;
	position: absolute;
	bottom: 56px;
	left: 0px;
	z-index: 2;
	background: #0b0102;
`;

const PlayButton = styled.img`
	display: none;
	position: absolute;
    z-index: 5;
	top: 50%;
	margin-top: -100.5px;
	left: 50%;
    margin-left: -78.5px;
`;

const Video = styled.video`
	height: 720px;
	width: 1280px;
	background: #0b0102;
	position: absolute;
	bottom: 56px;
	left: 0px;
	z-index: 1;
`;


class Watch extends Component {
	constructor(props) {
		super(props);
		this.state = { play: true };
	}

	changeVolume = () => {
		this.video.volume = .5;
		this.placeholder.style.display = 'none';
	}


	play = () => {

		if (this.state.play) {
			this.setState({ play: false });
			this.video.pause();


		} else {
			this.setState({ play: true });
			this.video.play();
		}	
	}


	componentDidMount() {
		// this.play();
	}

	render() {
		return (
			<Container onClick={this.play}>
				<Placeholder ref={(placeholder) => { this.placeholder = placeholder }} />
				<PlayButton src="assets/images/play-btn.png" style={this.state.play ? {display: 'none'} : { display: 'block' }} />
				<Video src="assets/video/watch.mp4" ref={(video) => { this.video = video }} onPlay={this.changeVolume} autoPlay />
			</Container>
		)
	};
}

export default Watch;
