import React from 'react';
import ReactDOM from 'react-dom';
import styled from 'styled-components';

// for react jPlayer
import { Provider } from 'react-redux';
import { createStore, combineReducers } from 'redux';
import { reducer as jPlayers } from 'react-jplayer';

import AudioPlayer from '../../components/AudioPlayer';

import ButtonGradient from '../../components/ButtonGradient';


const Container = styled.div`
	text-align: center;
`;

const MusicChoiceLogo = styled.img`
	margin-top: 20px;
`;

const Heading = styled.h1`
	color: #00aaf4;
	font-size: 105px;
	line-height: 105px;
	margin: 50px 0 35px 0;
	font-weight: bold;
	font-family:  cera_probold, Arial, Helvetica, sans-serif;
`;

const MainCopy = styled.p`
	color: #455051;
	font-size: 37px;
	line-height: 37px;
	max-width: 990px;
	margin: auto;
	font-weight: bold;
	font-family:  cera_prolight, Arial, Helvetica, sans-serif;
`;

const MusicButtonsRow = styled.div`
	margin-top: 60px;
	flex-flow: row nowrap;
`;

const ButtonSpacer = styled.span`
	margin-left: 45px;
`

/* Pass the jPlayer reducer and it's initialStates to the store */
const store = createStore(combineReducers({ jPlayers }));

ReactDOM.render((
	<Container>
		<MusicChoiceLogo src="assets/images/music-choice.png"/>
		<Heading>
			Hear the difference!
		</Heading>
		<MainCopy>
			Use the buttons below to select a genre and discover just how great your music can sound.
		</MainCopy>
		<MusicButtonsRow>
			<ButtonGradient text="Rock"/>
			<ButtonSpacer />
			<ButtonGradient text="Jazz"/>
			<ButtonSpacer />
			<ButtonGradient text="Country"/>
			<ButtonSpacer />
			<ButtonGradient text="Hip Hop"/>
		</MusicButtonsRow>
		<AudioPlayer />
	</Container>
));

export default listen;
