import React, { Component, Fragment } from 'react';
import { withRouter } from 'react-router';


class Timer extends Component {
	constructor(props) {
		super(props);
		this.state = this.state = {
			startingTime: 180,
			seconds: 0,
			activeTimer: false
		};
		this.timer = 0;
		this.runTimer = this.runTimer.bind(this);
		this.timerListener = this.timerListener.bind(this);
		this.countDown = this.countDown.bind(this);
	}

	componentDidMount() {
		this.timerListener();
	}

	timerListener() {
		document.body.addEventListener('click', () => this.runTimer());
	}

	runTimer() {
		console.log('start');

		this.setState({seconds: this.state.startingTime});

		if(this.state.activeTimer === false) {
			this.setState({activeTimer: true});
			this.timer = setInterval(this.countDown, 1000);
		}
	}

	countDown() {
		let seconds = this.state.seconds - 1;
		this.setState({
			seconds: seconds,
		});
		console.log(this.state.seconds);

		if (this.state.seconds === 0) {
			clearInterval(this.timer);
			this.setState({activeTimer: false});
			this.props.history.push('/')
		}
	}

	render() {
		return (
			<Fragment></Fragment>
		);
	}
}

export default withRouter(Timer);
