import React, { Component} from 'react';
import styled from 'styled-components';


const SlideContainer = styled.div`
	width: 850px;
	margin: auto;
`;

const VolumeSlider = styled.input`
	:hover {
	opacity: 1; /* Fully shown on mouse-over */
	}


	/* The slider handle 
	(use -webkit- (Chrome, Opera, Safari, Edge) and -moz- (Firefox) to override default look) */ 

	::-webkit-slider-thumb {
		-webkit-appearance: none;
		appearance: none;
		width: 25px;
		height: 25px;
		border-color: #00aaf4;
		border-radius: 50%; 
		background: #FFFFFF;
		cursor: pointer;
	}
	::-moz-range-thumb {
		width: 25px;
		height: 25px;
		border: #00aaf4;
		border-radius: 50%;
		background: #FFFFFF;
		cursor: pointer;
	}

	-webkit-appearance: none;
	width: 100%;
	height: 25px;
	border-radius: 15px;   
	background: #d3d3d3;
	outline: none;
	opacity: 0.7;
	-webkit-transition: .2s;
	transition: opacity .2s;	
`;


class Volume extends Component {
render () {
		return (
			<SlideContainer>
				<VolumeSlider type="range" min="0" max="100" value={this.props.value} onChange={this.props.changed}/>
			</SlideContainer>
		)
	}
}

export default Volume;
