import React, {Fragment} from 'react';
import styled from 'styled-components';

const Button = styled.button`
	::before {
		content: '';
		border-radius: 40px;
		background-image: linear-gradient(135deg, #00aaf4 , #00d258);
		padding: 32.5px 118px;
		top: -3px;
		right: -3px;
		position: absolute;
		z-index: -1;
	  }
	  :hover, :focus {
		color: #ffffff;
		background-image: linear-gradient(135deg, #00aaf4 , #00d258);
		outline:0;
	  }
	color: #00aaf4;
	font-size: 23px;
	font-family:  cera_probold, Arial, Helvetica, sans-serif;
	width: 230px;
	padding: 15px 0px;
	border-radius: 35px;
	border: none;
	cursor: pointer;
	margin-bottom: 20px;
	position: relative;
`;

 const playAudio = function () {

// Update the Button
var pause = this.innerHTML === 'pause!';
ctrl.innerHTML = pause ? 'play!' : 'pause!';

// Update the Audio
var method = pause ? 'pause' : 'play';
yourAudio[method]();

// Prevent Default Action
return false;
};

const button = (props) => (

	<Fragment>
		<audio id="yourAudio" preload='none'>
		<source src="assets/audio/test.mp3" type="audio/mpeg" />
			Your browser does not support the audio element.
		</audio>
		<Button id="audioControl" onClick={playAudio()}>{props.text}</Button>
	</Fragment>
)


export default button;
