import React, { Component } from 'react';
import styled from 'styled-components';

const Container = styled.div`
	background: #000;
	padding-top: 21px;
`;

const Video = styled.video`
	height: 720px;
	width: 1280px;
	background: #0b0102;
	position: absolute;
	bottom: 56px;
	left: 0px;
	z-index: 1;
`;

const Placeholder = styled.img`
	height: 720px;
	width: 1280px;
	position: absolute;
	bottom: 56px;
	left: 0px;
	z-index: 2;
`;

class Watch extends Component {
	constructor(props) {
		super(props);
		this.state = { play: false };
	}

	play = () => {
		if (this.state.play) {
			this.setState({ play: false });
			this.video.pause();
			this.placeholder.style.display = 'inline';
		} else {
			this.setState({ play: true });
			this.video.play();
			this.placeholder.style.display = 'none';
		}
	}

	render() {
		return (
			<Container>
				<Placeholder src="assets/images/watch_placeholder.jpg" alt="Placeholder" className="placeholder" ref={(placeholder) => { this.placeholder = placeholder }} onClick={this.play} />
				<Video src="assets/video/jbl-xtreme-2.mp4" ref={(video) => { this.video = video }} controls />
			</Container>
		)
	};
}

export default Watch;
