import React, { Component, Fragment } from 'react';
import styled from 'styled-components';

const Button = styled.button`
	::before {
		content: '';
		border-radius: 40px;
		background-image: linear-gradient(135deg, #00aaf4 , #00d258);
		padding: 32.5px 118px;
		top: -3px;
		right: -3px;
		position: absolute;
		z-index: -1;
	}
	:hover, :focus {
		color: #ffffff;
		background-image: linear-gradient(135deg, #00aaf4 , #00d258);
		outline:0;
	}
	color: #00aaf4;
	font-size: 23px;
	font-family: cera_probold, Arial, Helvetica, sans-serif;
	width: 230px;
	padding: 15px 0px;
	border-radius: 35px;
	border: none;
	cursor: pointer;
	margin: 0 22.5px 20px;
	position: relative;
`;


class AudioButton extends Component {
	constructor(props) {
		super(props);
		this.state = { 
			play: false,
			volume: 0.1 
		};
	}
	// Toggled play state
	play = () => {
		if (this.state.play) {
			this.setState({ play: false });
			this.audio.pause();
		} else {
			this.setState({ play: true });
			this.audio.play();
		}
	}

		setVolume = function(val)
		{
			var player = document.getElementsByClassName('audioPlayer');
			console.log('Before: ' + player.volume);
			player.volume = val / 100;
			console.log('After: ' + player.volume);
		}

	componentWillReceiveProps() {
		this.setState({ play: true });
	}

	render() {
		return (<Fragment>
			<audio className="audioPlayer" ref={(audio) => { this.audio = audio }}>
				<source src={this.props.musicSrc} type="audio/mpeg" />
				Your browser does not support the audio element.
			</audio>
			<Button onClick={this.play}>{this.props.text}</Button>
			<input id="vol-control" type="range" min="0" max="100" step="1" oninput="SetVolume(this.value)" onchange="SetVolume(this.value)"></input>
		</Fragment>
		)
	}
}

export default AudioButton;
