import React, { Component, Fragment } from 'react';
import styled from 'styled-components';

import AudioButton from '../Partials/AudioButton';
import Volume from '../Partials/Volume';

const Container = styled.div`
	text-align: center;
	padding-top: 44px;
`;

const MusicChoiceLogo = styled.img`
	margin-top: 20px;
`;

const Heading = styled.h1`
	color: #00aaf4;
	font-size: 105px;
	line-height: 105px;
	margin: 40px 0 35px 0;
	font-weight: bold;
	font-family: cera_probold, Arial, Helvetica, sans-serif;
`;

const MainCopy = styled.p`
	color: #455051;
	font-size: 37px;
	line-height: 37px;
	max-width: 990px;
	margin: auto;
	font-weight: bold;
	font-family: cera_prolight, Arial, Helvetica, sans-serif;
`;

const MusicButtonsRow = styled.div`
	margin-top: 60px;
	margin-bottom: 50px;
	flex-flow: row nowrap;
`;

const Footnote = styled.p`
	color: #455051;
	font-size: 18px;
	font-family: cera_prolight, Arial, Helvetica, sans-serif;
	margin: 60px 20px 10px 20px;
	text-align: left;
`;

class Listen extends Component {
	constructor(props) {
		super(props);
		this.state = {
			volume: 1,
			sliderValue: 50,
			prevSong: null
		};
	}

	playSongHandler = (song) => {
		this.setState({ prevSong: song });
		console.log(this.state.prevSong)

	}

	changeSliderValueHandler = (event) => {
		const newSliderValue = event.target.value;
		this.setState({
			sliderValue: newSliderValue,
			volume: newSliderValue / 100
		})
	}

	render() {
		return (
			<Fragment>
				<Container>
					<MusicChoiceLogo src="assets/images/music-choice.png" alt="music choice" />
					<Heading>
						Hear the difference!
					</Heading>
					<MainCopy>
						Use the buttons below to select a genre and discover just how great your music can sound.
					</MainCopy>
					<MusicButtonsRow>
						<AudioButton volume={this.state.volume} prevSong={this.state.prevSong} currentSong="Rock" songIsPlaying={this.state.songIsPlaying} clicked={this.playSongHandler} text="Rock" musicSrc="assets/audio/test.mp3" />
						<AudioButton volume={this.state.volume} prevSong={this.state.prevSong} currentSong="Jazz" songIsPlaying={this.state.songIsPlaying} clicked={this.playSongHandler} text="Jazz" musicSrc="assets/audio/test.mp3" />
						<AudioButton volume={this.state.volume} prevSong={this.state.prevSong} currentSong="Country" songIsPlaying={this.state.songIsPlaying} clicked={this.playSongHandler} text="Country" musicSrc="assets/audio/test.mp3" />
						<AudioButton volume={this.state.volume} prevSong={this.state.prevSong} currentSong="Hip Hop" songIsPlaying={this.state.songIsPlaying} clicked={this.playSongHandler} text="Hip Hop" musicSrc="assets/audio/test.mp3" />
					</MusicButtonsRow>
					<Volume sliderVal={this.state.rangeSliderValue} changed={this.changeSliderValueHandler} />
					<Footnote>Music provided by Music Choice, <br />available now on Contour.</Footnote>
				</Container>
			</Fragment>
		)
	}
}

export default Listen;
